weaponAPI:
{
  "openapi": "3.0.1",
  "info": {
    "title": "GBU-X Demo FOM Service API Version 1.0.1",
    "description": "API for calculating trajectories and retrieving information about a GBU-X weapon variants.",
    "version": "1.0.1"
  },
  "paths": {
    "/calculation": {
      "post": {
        "tags": [
          "CalculationApi"
        ],
        "summary": "Provides calculation results.",
        "description": "Provides all requested calculation results for the specified weapon varaint utilizing the conditions set for calculation.",
        "parameters": [
          {
            "name": "api-version",
            "in": "query",
            "description": "The requested API version",
            "schema": {
              "type": "string",
              "default": "1"
            }
          },
          {
            "name": "accept-Version",
            "in": "header",
            "schema": {
              "type": "string"
            }
          }
        ],
        "requestBody": {
          "description": "A JSON-formatted string of a collection of calculation parameters to be used for calculation. The calculation parameters should follow the prescribed format for calculation parameters as listed in this schema.",
          "content": {
            "application/json; api-version=1": {
              "schema": {
                "type": "string"
              }
            },
            "text/json; api-version=1": {
              "schema": {
                "type": "string"
              }
            },
            "application/*+json; api-version=1": {
              "schema": {
                "type": "string"
              }
            }
          }
        },
        "responses": {
          "200": {
            "description": "A JSON-formatted string of calculation results. The calculation results should follow the prescribed format for calculation results as listed in this schema.",
            "content": {
              "text/plain; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "application/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "text/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              }
            }
          },
          "400": {
            "description": "Invalid Parameters"
          },
          "500": {
            "description": "Internal Exception",
            "content": {
              "text/plain; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "application/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "text/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    },
    "/information": {
      "get": {
        "tags": [
          "InformationApi"
        ],
        "summary": "Provides information regarding the FOM.",
        "description": "Provides information regarding the FOM and its weapon variants.",
        "parameters": [
          {
            "name": "api-version",
            "in": "query",
            "description": "The requested API version",
            "schema": {
              "type": "string",
              "default": "1"
            }
          },
          {
            "name": "accept-Version",
            "in": "header",
            "schema": {
              "type": "string"
            }
          }
        ],
        "responses": {
          "200": {
            "description": "A JSON-formatted string of FOM information. The information should follow the prescribed format for FOM information as listed in this schema.",
            "content": {
              "text/plain; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "application/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "text/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              }
            }
          },
          "500": {
            "description": "Internal Exception",
            "content": {
              "text/plain; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "application/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              },
              "text/json; api-version=1": {
                "schema": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "CalculationParameters": {
        "required": [
          "weaponId",
          "impactConditions",
          "releaseConditions",
          "resultsRequested",
          "targetLocation"
        ],
        "type": "object",
        "properties": {
          "weaponId": {
            "type": "integer",
            "description": "The unique weapon variant id that will be used when calculating the parameters.",
            "format": "int32"
          },
          "resultsRequested": {
            "$ref": "#/components/schemas/ResultsRequested"
          },
          "targetLocation": {
            "$ref": "#/components/schemas/Location"
          },
          "impactConditions": {
            "$ref": "#/components/schemas/ImpactConditions"
          },
          "releaseConditions": {
            "$ref": "#/components/schemas/ReleaseConditions"
          }
        },
        "additionalProperties": false,
        "description": "Item for holding calculation conditions to be used when requesting calculation."
      },
      "CalculationResult": {
        "type": "object",
        "properties": {
          "errorMessages": {
            "description": "Collection of applicable error messages related to the calculating of the provided parameters.",
            "type": "array",
            "items": {
              "type": "string"
            }
          },
          "flyout": {
            "nullable": true,
            "$ref": "#/components/schemas/TrajectoryResults"
          },
          "inRangeFootprint": {
            "nullable": true,
            "$ref": "#/components/schemas/RegionResults"
          },
          "inZoneFootprint": {
            "nullable": true,
            "$ref": "#/components/schemas/RegionResults"
          },
          "inRangeLAR": {
            "nullable": true,
            "$ref": "#/components/schemas/RegionResults"
          },
          "inZoneLAR": {
            "nullable": true,
            "$ref": "#/components/schemas/RegionResults"
          },
          "inRangeMinMaxLAR": {
            "nullable": true,
            "$ref": "#/components/schemas/MinMaxRegion"
          },
          "inZoneMinMaxLAR": {
            "nullable": true,
            "$ref": "#/components/schemas/MinMaxRegion"
          }
        },
        "additionalProperties": false,
        "description": "Holds all calculation results for a calculation request."
      },
      "CalculationResults": {
        "type": "object",
        "properties": {
          "results": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/CalculationResult"
            },
            "description": "Collection of calculation results."
          }
        },
        "additionalProperties": false,
        "description": "Aggregated results from one or more calculation requests."
      },
      "Constraint": {
        "type": "object",
        "properties": {
          "minimum": {
            "type": "number",
            "description": "The minimum value of the constraint.",
            "format": "double",
            "nullable": true
          },
          "maximum": {
            "type": "number",
            "description": "The maximum value of the constraint.",
            "format": "double",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "A minimum and maximum constraint"
      },
      "Coordinate": {
        "type": "object",
        "properties": {
          "latitude": {
            "type": "number",
            "description": "The WGS-84 latitude position in decimal degrees.",
            "format": "double",
            "nullable": true
          },
          "longitude": {
            "type": "number",
            "description": "The WGS-84 longitude position in decimal degrees.",
            "format": "double",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Holds latitude and longtiude position data."
      },
      "FOMInformation": {
        "type": "object",
        "required": [
          "longDescription",
          "shortDescription",
          "version",
          "weaponVariants"
        ],
        "properties": {
          "longDescription": {
            "type": "string",
            "description": "In-depth information about the FOM.",
            "nullable": false
          },
          "shortDescription": {
            "type": "string",
            "description": "Short description of the FOM.",
            "nullable": false
          },
          "version": {
            "type": "string",
            "description": "Current version of the FOM.",
            "nullable": false
          },
          "weaponVariants": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/WeaponVariant"
            },
            "description": "Weapon Variants the FOM supports.",
            "nullable": false
          }
        },
        "additionalProperties": false,
        "description": "All information concerning the FOM."
      },
      "FuzeData": {
        "required": [
          "name"
        ],
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the fuze."
          },
          "armTimes": {
            "type": "array",
            "items": {
              "type": "number",
              "format": "double"
            },
            "description": "The arm times of the fuze in seconds.",
            "nullable": true
          },
          "functionTimes": {
            "type": "array",
            "items": {
              "type": "number",
              "format": "double"
            },
            "description": "The function times of the fuze in milliseconds.",
            "nullable": true
          },
          "hobSettings": {
            "type": "array",
            "items": {
              "type": "number",
              "format": "double"
            },
            "description": "The potential height of burst values of the fuze in feet.",
            "nullable": true
          },
          "locations": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/FuzeLocation"
            },
            "description": "The valid locations for the fuze on the weapon.",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Basic fuze data information related to a particular fuze for a weapon."
      },
      "FuzeLocation": {
        "enum": [
          "Nose",
          "Tail",
          "NoseAndTail",
          "Integral"
        ],
        "type": "string",
        "description": "Available fuze locations within a weapon."
      },
      "ImpactConditions": {
        "type": "object",
        "required": [
          "impactAngle"
        ],
        "properties": {
          "confidence": {
            "type": "integer",
            "description": "The confidence level of the horizontal and vertical accuracies of the DPI. The value should be either 50 or 90 percent.",
            "format": "int32",
            "nullable": true
          },
          "horizontalAccuracy": {
            "maximum": 1000,
            "minimum": 0,
            "type": "number",
            "description": "The horizontal accuracy (in feet) of the Circular Error Probable (CEP) of the DPI location.  The value range should be between 0 - 1000 feet.",
            "format": "double",
            "nullable": true
          },
          "impactAngle": {
            "maximum": 90,
            "minimum": 0,
            "type": "number",
            "description": "The desired terminal impact angle (in degrees) of the weapon relative to local level (0 = fly into a vertical wall, 90 = fly straight down). The value range should be between 0 - 90 degrees.",
            "format": "double",
            "nullable": true
          },
          "impactAngleLowerTolerance": {
            "maximum": 90,
            "minimum": 0,
            "type": "number",
            "description": "The Delta value (in degrees) to apply to the commanded impact angle to determine the acceptable lower limit for impact angles (e.g., a value of 10 associated with an angle of 65 results in a minimum acceptable impact angle of 55 degrees). The value range should be between 0 - 90 degrees.",
            "format": "double",
            "nullable": true
          },
          "impactAngleUpperTolerance": {
            "maximum": 90,
            "minimum": 0,
            "type": "number",
            "description": "The Delta value (in degrees) to apply to the commanded impact angle to determine the acceptable upper limit for impact angles (e.g., a value of 10 associated with an angle of 65 results in a maximum acceptable impact angle of 75 degrees). The value range should be between 0 - 90 degrees.",
            "format": "double",
            "nullable": true
          },
          "impactAzimuth": {
            "maximum": 359,
            "minimum": 0,
            "type": "number",
            "description": "The desired impact azimuth relative to north (0 = fly north, 90 = fly east). The value range should be between 0 - 359.9 degrees.",
            "format": "double",
            "nullable": true
          },
          "impactAzimuthLeftTolerance": {
            "maximum": 359,
            "minimum": 0,
            "type": "number",
            "description": "The Delta value (in degrees) to apply to the commanded impact azimuth to determine the range of acceptable impact azimuths to the left of the weapon course (e.g., a value of 10 with an azimuth of 45 results in impact azimuths from 35 to 45 being considered). The value range should be between 0 - 359.9 degrees.",
            "format": "double",
            "nullable": true
          },
          "impactAzimuthRightTolerance": {
            "maximum": 359,
            "minimum": 0,
            "type": "number",
            "description": "The Delta value (in degrees) to apply to the commanded impact azimuth to determine the range of acceptable impact azimuths to the right of the weapon course (e.g., a value of 10 with an azimuth of 45 results in impact azimuths from 45 to 55 being considered). The value range should be between 0 - 359.9 degrees.",
            "format": "double",
            "nullable": true
          },
          "minImpactVelocity": {
            "maximum": 26874,
            "minimum": 0,
            "type": "number",
            "description": "The desired minimum velocity of the weapon at impact in feet per second. The value range should be between 0 - 26874 feet per second.",
            "format": "double",
            "nullable": true
          },
          "orientation": {
            "$ref": "#/components/schemas/TargetOrientation"
          },
          "verticalAccuracy": {
            "maximum": 1000,
            "minimum": 0,
            "type": "number",
            "description": "The vertical accuracy Linear Error Probable (LEP) of the JDPI location in feet. The value range should be between 0 and 1000 feet.",
            "format": "double",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Contains the impact conditions to be used for FOM calculation requests."
      },
      "ImpactConstraints": {
        "type": "object",
        "properties": {
          "impactAngle": {
            "$ref": "#/components/schemas/Constraint"
          },
          "impactVelocity": {
            "$ref": "#/components/schemas/Constraint"
          }
        },
        "additionalProperties": false,
        "description": "Impact constraints for a weapon variant."
      },
      "LaserConstraints": {
        "type": "object",
        "properties": {
          "laserCodes": {
            "type": "array",
            "items": {
              "type": "integer",
              "description": "Laser code for the weapon variant.",
              "format": "int32"
            },
            "description": "Valid laser codes for the weapon variant.",
            "nullable": true
          },
          "laserPulseLogicSettings": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/LaserPulseLogicSetting"
            },
            "description": "Laser pulse logic settings of the weapon variant.",
            "nullable": true
          }
        }
      },
      "LaserPulseLogicSetting": {
        "enum": [
          "ShortLast",
          "LongLast"
        ],
        "type": "string",
        "description": "Potential Laser Pulse Logic Setting within a weapon."
      },
      "LaserWeaponVariant": {
        "allOf": [
          {
            "$ref": "#/components/schemas/WeaponVariant"
          },
          {
            "type": "object",
            "required": [
              "laserConstraints"
            ],
            "properties": {
              "laserConstraints": {
                "$ref": "#/components/schemas/LaserConstraints"
              }
            },
            "description": "All the information concerning a specific laser weapon variant."
          }
        ]
      },
      "Location": {
        "required": [
          "altitude",
          "coordinate"
        ],
        "type": "object",
        "properties": {
          "coordinate": {
            "$ref": "#/components/schemas/Coordinate"
          },
          "altitude": {
            "type": "number",
            "description": "The height above elevation (HAE) altitude position of the location in feet.",
            "format": "double",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Holds location information."
      },
      "MinMaxRegion": {
        "type": "object",
        "properties": {
          "maxRegion": {
            "$ref": "#/components/schemas/RegionResults"
          },
          "minRegion": {
            "$ref": "#/components/schemas/RegionResults"
          }
        },
        "additionalProperties": false,
        "description": "The minimum and maximum data of a Launch Acceptability Region (LAR)."
      },
      "RegionResults": {
        "type": "object",
        "properties": {
          "errorMessages": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Applicable error messages for the region calculation.",
            "nullable": true
          },
          "boundingCoordinates": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/Coordinate"
            },
            "description": "The bounding coordinates around the region.",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Holds the calculated results for a region."
      },
      "ReleaseConditions": {
        "type": "object",
        "required": [
          "aircraftAirspeed",
          "aircraftCourse",
          "aircraftFlightPathAngle"
        ],
        "properties": {
          "aircraftAirspeed": {
            "maximum": 2,
            "minimum": 0.1,
            "type": "number",
            "description": "The aircraft true airspeed (Mach) at the weapon release point.  The value range should be between 0.1 and 2.0 Mach.",
            "format": "double",
            "nullable": true
          },
          "aircraftCourse": {
            "maximum": 359.9,
            "minimum": 0,
            "type": "number",
            "description": "The aircraft true course (in degrees true) at the weapon release point (direction of the horizontal component of velocity, 0 = north, 90 = east). The value range should be between 0.0 and 359.9 degrees.",
            "format": "double",
            "nullable": true
          },
          "aircraftFlightPathAngle": {
            "maximum": 45,
            "minimum": -45,
            "type": "number",
            "description": "The flight path angle (in degrees) of the aircraft at the weapon release point. A positive value indicates a climb. The value range should be between -45.0 - 45.0 degrees.",
            "format": "double",
            "nullable": true
          },
          "aircraftRoll": {
            "maximum": 90,
            "minimum": -90,
            "type": "number",
            "description": "The roll angle of the aircraft (in degerees) at the weapon release point (positive value is left wing up; 0 is a wings level release). The value range should be between -90.0 - 90.0 degrees.",
            "format": "double",
            "nullable": true
          },
          "aircraftAltitude": {
            "type": "number",
            "description": "The height above elevation (HAE) altitude position (in feet) of the aircraft at the weapon relase point.",
            "format": "double",
            "nullable": true
          },
          "launchDateTime": {
            "type": "string",
            "description": "The date and time (Zulu) at the weapon release point.",
            "nullable": true
          },
          "releaseLocation": {
            "$ref": "#/components/schemas/Coordinate"
          }
        },
        "additionalProperties": false,
        "description": "Contains the release conditions to be used for FOM calculation requests."
      },
      "ReleaseConstraints": {
        "type": "object",
        "properties": {
          "airspeedInKCAS": {
            "$ref": "#/components/schemas/Constraint"
          },
          "airspeedInMach": {
            "$ref": "#/components/schemas/Constraint"
          },
          "altitudeInFeetAGL": {
            "$ref": "#/components/schemas/Constraint"
          },
          "altitudeInFeetMSL": {
            "$ref": "#/components/schemas/Constraint"
          },
          "pitch": {
            "$ref": "#/components/schemas/Constraint"
          },
          "roll": {
            "$ref": "#/components/schemas/Constraint"
          }
        },
        "additionalProperties": false,
        "description": "Release constraints for a weapon variant."
      },
      "ResultsRequested": {
        "type": "object",
        "properties": {
          "calculateAll": {
            "type": "boolean",
            "description": "Indicates if all results are being requested."
          },
          "calculateFlyout": {
            "type": "boolean",
            "description": "Indicates if the flyout trajectory results are being requested."
          },
          "calculateInZoneLAR": {
            "type": "boolean",
            "description": "Indicates if the In Zone LAR results are being requested."
          },
          "calculateInRangeLAR": {
            "type": "boolean",
            "description": "Indicates if the In Range LAR results are being requested."
          },
          "calculateInZoneFootprint": {
            "type": "boolean",
            "description": "Indicates if the In Zone Footprint results are being requested."
          },
          "calculateInRangeFootprint": {
            "type": "boolean",
            "description": "Indicates if the In Range Footprint results are being requested."
          },
          "calculateInZoneMinMaxLAR": {
            "type": "boolean",
            "description": "Indicates if the In Zone Min Max LAR results are being requested."
          },
          "calculateInRangeMinMaxLAR": {
            "type": "boolean",
            "description": "Indicates if the In Range Min Max LAR results are being requested."
          }
        },
        "additionalProperties": false,
        "description": "Holds values to indicate which results are being requested as part of the calculation request. If a value is excluded, it will be treated as false."
      },
      "TargetOrientation": {
        "enum": [
          "Vertical",
          "Horizontal"
        ],
        "type": "string",
        "description": "The orientation values for a target."
      },
      "TrajectoryPoint": {
        "required": [
          "elapsedTime",
          "location"
        ],
        "type": "object",
        "properties": {
          "location": {
            "$ref": "#/components/schemas/Location"
          },
          "elapsedTime": {
            "type": "number",
            "description": "The elapsed time (seconds) from the beginning of flight.",
            "format": "double",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Holds data for a specific point on a flyout trajectory."
      },
      "TrajectoryResults": {
        "required": [
          "hitTarget"
        ],
        "type": "object",
        "properties": {
          "errorMessages": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "description": "Error messages for the trajectory calculation.",
            "nullable": true
          },
          "hitTarget": {
            "type": "boolean",
            "description": "Indicates the weapon \\\"hit\\\" the target."
          },
          "impactAngle": {
            "maximum": 90,
            "minimum": 0,
            "type": "number",
            "description": "The predicted impact angle (in degrees). The value range should be between 0.0 - 90.0 degrees.",
            "format": "double",
            "nullable": true
          },
          "impactAzimuth": {
            "maximum": 359,
            "minimum": 0,
            "type": "number",
            "description": "The predicted impact azimuth (in degrees). The value range should be between 0.0 - 359.9 degrees.",
            "format": "double",
            "nullable": true
          },
          "impactLocation": {
            "$ref": "#/components/schemas/Location"
          },
          "impactVelocity": {
            "maximum": 26874,
            "minimum": 0,
            "type": "number",
            "description": "The predicted impact velocity in feet per second. The value range should be between 0.0 - 26874.0 feet per second.",
            "format": "double",
            "nullable": true
          },
          "path": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/TrajectoryPoint"
            },
            "description": "A list of coordinates and elevations that the trajectory traverses paired with their respective elapsed times.",
            "nullable": true
          },
          "timeOfFlight": {
            "maximum": 1500,
            "minimum": 0,
            "type": "number",
            "description": "The predicted time of flight (seconds) from weapon release to impact. The value range should be between 0.0 - 1500.0 seconds.",
            "format": "double",
            "nullable": true
          }
        },
        "additionalProperties": false,
        "description": "Contains the results of a calculated flyout trajectory."
      },
      "WeaponVariant": {
        "required": [
          "weaponId",
          "fuzeData",
          "impactConstraints",
          "name",
          "releaseConstraints",
          "weaponAPI",
          "weaponFamily"
        ],
        "type": "object",
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the weapon variant."
          },
          "weaponId": {
            "type": "integer",
            "description": "The FPMID for the weapon variant.",
            "format": "int32"
          },
          "longDescription": {
            "type": "string",
            "description": "In-depth information about the weapon variant.",
            "nullable": true
          },
          "shortDescription": {
            "type": "string",
            "description": "Short description of the weapon variant.",
            "nullable": true
          },
          "fuzeData": {
            "type": "array",
            "items": {
              "$ref": "#/components/schemas/FuzeData"
            },
            "description": "All fuze data information concerning a weapon variant."
          },
          "impactConstraints": {
            "$ref": "#/components/schemas/ImpactConstraints"
          },
          "releaseConstraints": {
            "$ref": "#/components/schemas/ReleaseConstraints"
          },
          "weaponAPI": {
            "type": "string",
            "description": "The most current API for the weapon FOM service."
          },
          "weaponFamily": {
